    operation: 'calculate',
    values: numbers,
    metadata: {
        sum: sum,
        average: average,
        count: numbers.length,
        generatedAt: new Date().toISOString()
    },
    requestId: pm.variables.replaceIn('{{$guid}}'),
    environment: pm.environment.name
};

// Update request body
pm.request.body.raw = JSON.stringify(requestBody, null, 2);

// Add custom header
